import React, { useEffect, useState } from "react";
import Nav from "../Global/Nav";
import { Container, Button, Row, Form, Col } from "react-bootstrap";

const AllEmployees = () => {
  let dateObj = new Date();
  let year = dateObj.getFullYear();
  let month = dateObj.getMonth();
  let day = dateObj.getDate();

  const finalMonth = () => {
    if ((month <= 10) & (month === 0)) {
      return `0${month + 1}`;
    } else {
      return month;
    }
  };
  const finalDay = () => {
    if (day <= 10) {
      return `0${day}`;
    } else {
      return day;
    }
  };
  let date = `${year}-${finalMonth()}-${finalDay()}`;

  // get employees
  useEffect(() => {
    fetch(`${URL}/employees`)
      .then((res) => res.json())
      .then((json) => setEmployees(json));
  }, []);

  const URL = "http://localhost:8080";
  const [employees, setEmployees] = useState([]);

  // EDIT MODE
  const [editMode, setEditMode] = useState(false);

  // SET UPDATED INPUTS
  const [newAddressUpdated, setNewAddressUpdated] = useState(false);
  const [newDateofbirthUpdated, setNewDateofbirthUpdated] = useState(false);
  const [newDegreeUpdated, setNewDegreeUpdated] = useState(false);
  const [newDepartmentUpdated, setNewDepartmentUpdated] = useState(false);
  const [newDesignationUpdated, setNewDesignationUpdated] = useState(false);
  const [newEmailUpdated, setNewEmailUpdated] = useState(false);
  const [newFirstnameUpdated, setNewFirstnameUpdated] = useState(false);
  const [newGenderUpdated, setNewGenderUpdated] = useState(false);
  const [newLastnameUpdated, setNewLastnameUpdated] = useState(false);
  const [newMobileUpdated, setNewMobileUpdated] = useState(false);

  // SET NEW FORM
  const [newAddress, setNewAddress] = useState("");
  const [newDateofbirth, setNewDateofbirth] = useState("");
  const [newDegree, setNewDegree] = useState("");
  const [newDepartment, setNewDepartment] = useState("");
  const [newDesignation, setNewDesignation] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newFirstname, setNewFirstname] = useState("");
  const [newGender, setNewGender] = useState("");
  const [newLastname, setNewLastname] = useState("");
  const [newMobile, setNewMobile] = useState("");

  // EDIT  Employee
  function editEmployee() {
    setEditMode(!editMode);
  }

  // UPDATE EMPLOYEE
  async function updateEmployee(e, id) {
    e.preventDefault();
    setEditMode(!editMode);

    const foundEmployee = employees.filter((employee) => {
      const { employeeid } = employee;
      if (employeeid === id) {
        return employee;
      }
    });

    // Post options
    const options = {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        employeeid: foundEmployee[0].employeeid,
        fname: `${
          newFirstnameUpdated ? newFirstname : foundEmployee[0].firstname
        }`,
        lname: `${
          newLastnameUpdated ? newLastname : foundEmployee[0].lastname
        }`,
        gender: `${newGenderUpdated ? newGender : foundEmployee[0].gender}`,
        mobile: `${newMobileUpdated ? newMobile : foundEmployee[0].mobile}`,
        designation: `${
          newDesignationUpdated ? newDesignation : foundEmployee[0].designation
        }`,
        department: `${
          newDepartmentUpdated ? newDepartment : foundEmployee[0].department
        }`,
        address: `${newAddressUpdated ? newAddress : foundEmployee[0].address}`,
        email: `${newEmailUpdated ? newEmail : foundEmployee[0].email}`,
        degree: `${newDegreeUpdated ? newDegree : foundEmployee[0].degree}`,
      }),
    };

    try {
      const fetchPromiseResponse = await fetch(
        `${URL}/update-employee`,
        options
      );
      if (!fetchPromiseResponse.ok) {
        console.log(
          `Something went wrong with fetch from server ${fetchPromiseResponse.status}`
        );
      }
      const jsonPromiseResponse = fetchPromiseResponse.json();

      jsonPromiseResponse.then((res) => {
        console.log(res);
      });
    } catch {
      (err) => {
        console.log(`FETCH FAILED: ${err}`);
      };
    }
  }

  return (
    <>
      <Nav />
      <Container>
        {employees.map((employee) => {
          const {
            address,
            dateofbirth,
            degree,
            department,
            designation,
            email,
            employeeid,
            firstname,
            gender,
            lastname,
            mobile,
          } = employee;

          return (
            <section
              className="employee-card"
              key={employeeid}
            >
              <Form
                onSubmit={(e) => updateEmployee(e, employeeid)}
                autoComplete="true"
              >
                <Row className="flex-nowrap">
                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        <input
                          style={{}}
                          className="radio"
                          type="radio"
                          name=""
                          id=""
                        />
                      </Col>
                    </Row>
                  </Col>
                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        <p>Image</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <img
                          src=""
                          alt="picturrrrrr"
                        />
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        <p>Employee ID: </p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <p> {employeeid} </p>
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        <p>Gender: </p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <>
                            <input
                              type="text"
                              name=""
                              id=""
                              onChange={(e) => {
                                setNewGender(e.target.value);
                                setNewGenderUpdated(true);
                              }}
                              placeholder={
                                editMode & !newGenderUpdated
                                  ? gender
                                  : newGenderUpdated & editMode
                                  ? newGender
                                  : gender
                              }
                            />
                          </>
                        ) : (
                          <p> {newGenderUpdated ? newGender : gender} </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        <p>Name: </p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <input
                            type="text"
                            name=""
                            onChange={(e) => {
                              setNewFirstname(e.target.value);
                              setNewFirstnameUpdated(true);
                            }}
                            placeholder={
                              editMode & !newFirstnameUpdated
                                ? firstname
                                : newFirstnameUpdated & editMode
                                ? newFirstname
                                : firstname
                            }
                          />
                        ) : (
                          <p>
                            {" "}
                            {newFirstnameUpdated
                              ? newFirstname
                              : firstname}{" "}
                          </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        <p>DOB</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <input
                            type="text"
                            onChange={(e) => {
                              setNewDateofbirth(e.target.value);
                              setNewDateofbirthUpdated(true);
                            }}
                            placeholder={
                              editMode & !newDateofbirthUpdated
                                ? dateofbirth
                                : newDateofbirthUpdated & editMode
                                ? newDateofbirth
                                : dateofbirth
                            }
                          />
                        ) : (
                          <p> {dateofbirth} </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        <p>Department </p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <input
                            type="text"
                            onChange={(e) => {
                              setNewDepartment(e.target.value);
                              setNewDepartmentUpdated(true);
                            }}
                            placeholder={
                              editMode & !newDepartmentUpdated
                                ? department
                                : newDepartmentUpdated & editMode
                                ? newDepartment
                                : department
                            }
                          />
                        ) : (
                          <p>
                            {" "}
                            {newDepartmentUpdated
                              ? newDepartment
                              : department}{" "}
                          </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        <p>Role</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <input
                            type="text"
                            onChange={(e) => {
                              setNewDesignation(e.target.value);
                              setNewDesignationUpdated(true);
                            }}
                            placeholder={
                              editMode & !newDesignationUpdated
                                ? designation
                                : newDesignationUpdated & editMode
                                ? newDesignation
                                : designation
                            }
                          />
                        ) : (
                          <p>
                            {newDesignationUpdated
                              ? newDesignation
                              : designation}
                          </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        <p>Degree</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <input
                            type="text"
                            onChange={(e) => {
                              setNewDegree(e.target.value);
                              setNewDegreeUpdated(true);
                            }}
                            placeholder={
                              editMode & !newDegreeUpdated
                                ? degree
                                : newDegreeUpdated & editMode
                                ? newDegree
                                : degree
                            }
                          />
                        ) : (
                          <p> {newDegreeUpdated ? newDegree : degree} </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        <p>Mobile</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <input
                            type="text"
                            onChange={(e) => {
                              setNewMobile(e.target.value);
                              setNewMobileUpdated(true);
                            }}
                            placeholder={
                              editMode & !newMobileUpdated
                                ? degree
                                : newMobileUpdated & editMode
                                ? newDegree
                                : degree
                            }
                          />
                        ) : (
                          <p> {newMobileUpdated ? newMobile : mobile} </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        <p>email</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <input
                            type="text"
                            onChange={(e) => {
                              setNewEmail(e.target.value);
                              setNewEmailUpdated(true);
                            }}
                            placeholder={
                              editMode & !newEmailUpdated
                                ? email
                                : newEmailUpdated & editMode
                                ? newEmail
                                : email
                            }
                          />
                        ) : (
                          <p> {newEmailUpdated ? newEmail : email} </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        <p>Address</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <input
                            type="text"
                            onChange={(e) => {
                              setNewAddress(e.target.value);
                              setNewAddressUpdated(true);
                            }}
                            placeholder={
                              editMode & !newAddressUpdated
                                ? address
                                : newAddressUpdated & editMode
                                ? newAddress
                                : address
                            }
                          />
                        ) : (
                          <p> {newAddressUpdated ? newAddress : address} </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        <p>join date</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <p> {date} </p>
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="1"
                        className="col-6"
                      >
                        {editMode ? (
                          <Button type="submit">UPDATE</Button>
                        ) : (
                          <div className="form-btns">
                            <Button
                              onClick={editEmployee}
                              type="button"
                            >
                              Edit
                            </Button>

                            <Button type="button">delete</Button>
                          </div>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  {/* END CONTAINER ROW */}
                </Row>
              </Form>
            </section>
          );
        })}
      </Container>
      <h1>ALL EMPLOYEES</h1>
    </>
  );
};

export default AllEmployees;
