import { React, useEffect, useState } from "react";
const URL = "http://localhost:8080";

const getEmployees = () => {
  const [employees, setEmployees] = useState([]);

  // get employees
  useEffect(() => {
    fetch(`${URL}/employees`)
      .then((res) => res.json())
      .then((json) => setEmployees(json));
  }, []);

  return employees;
};

const employees = getEmployees();

export default employees;
