import React, { useEffect, useState } from "react";
import Nav from "../Global/Nav";
import { Container, Form } from "react-bootstrap";

import EmployeeCard from "./EmployeeCard";

const AllEmployees = () => {
  let dateObj = new Date();
  let year = dateObj.getFullYear();
  let month = dateObj.getMonth();
  let day = dateObj.getDate();

  const finalMonth = () => {
    if ((month <= 10) & (month === 0)) {
      return `0${month + 1}`;
    } else {
      return month;
    }
  };

  const finalDay = () => {
    if (day <= 10) {
      return `0${day}`;
    } else {
      return day;
    }
  };
  let date = `${year}-${finalMonth()}-${finalDay()}`;

  const URL = "http://localhost:8080";
  // get employees
  useEffect(() => {
    fetch(`${URL}/employees`)
      .then((res) => res.json())
      .then((json) => setEmployees(json));
  }, []);
  const [employees, setEmployees] = useState([]);

  let filteredEmloyees = [];
  const [employeeNewArray, setemployeeNewArray] = useState([]);
  // SHOW UPDATE MODAL
  const [show, setShow] = useState(false);
  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);

  return (
    <>
      <Nav />

      <Container>
        <section className="employees">
          <div className="employees-header">
            <div>
              <h2>Employees</h2>
              <div className="header-search form-control-container">
                <span className="search-icon form-control-container-icon">
                  <i className="fa-solid fa-magnifying-glass"></i>
                </span>
                <Form.Control
                  className="header-search-input"
                  type="text"
                  placeholder="Search"
                  onChange={(e) => {
                    filteredEmloyees = employees.filter((employee) => {
                      const {
                        firstname,
                        lastname,
                        department,
                        employeeid,
                        designation,
                        email,
                        date,
                        mobile,
                      } = employee;
                      return (
                        firstname == e.target.value ||
                        lastname == e.target.value ||
                        department == e.target.value ||
                        employeeid == e.target.value ||
                        designation == e.target.value ||
                        department == e.target.value ||
                        email == e.target.value ||
                        date == e.target.value ||
                        mobile == e.target.value
                      );
                    });
                    setemployeeNewArray(filteredEmloyees);
                  }}
                />
              </div>
            </div>

            <div className="employees-header-controls">
              <span className="icon-container">
                <i
                  type="button"
                  onClick={() => {
                    handleShow;
                    console.log("workinggg");
                  }}
                  className="fa-solid fa-plus icon-container-icon"
                ></i>
              </span>
              <span className="icon-container">
                <i className="fa-solid fa-arrows-rotate icon-container-icon"></i>
              </span>
            </div>
          </div>
          {employeeNewArray.length > 0
            ? employeeNewArray.map((employee) => {
                const {
                  department,
                  designation,
                  email,
                  employeeid,
                  firstname,
                  lastname,
                  mobile,
                } = employee;

                return (
                  <EmployeeCard
                    // show={show}
                    // handleShow={handleShow}
                    URL={URL}
                    employees={employees}
                    key={employeeid}
                    employeeid={employeeid}
                    firstname={firstname}
                    lastname={lastname}
                    department={department}
                    designation={designation}
                    mobile={mobile}
                    email={email}
                    date={date}
                  />
                );
              })
            : employees.map((employee) => {
                const {
                  department,
                  designation,
                  email,
                  employeeid,
                  firstname,
                  lastname,
                  mobile,
                } = employee;

                return (
                  <EmployeeCard
                    URL={URL}
                    employees={employees}
                    key={employeeid}
                    employeeid={employeeid}
                    firstname={firstname}
                    lastname={lastname}
                    department={department}
                    designation={designation}
                    mobile={mobile}
                    email={email}
                    date={date}
                  />
                );
              })}
        </section>
      </Container>
      <Modal
        show={show}
        onHide={handleClose}
        dialogClassName="modal-100vh"
      >
        <Modal.Header closeButton>
          <Modal.Title>
            <i className="fa-solid fa-user"></i>
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group
              className="mb-3"
              controlId="exampleForm.ControlInput1"
            >
              <Form.Label>Name</Form.Label>
              <div className="form-control-container ">
                <Form.Control
                  className="form-control-container-input"
                  nam
                  e="name"
                  defaultValue={
                    (btnValue === employeeid) & !newFirstnameUpdated
                      ? firstname + " " + lastname
                      : newFirstnameUpdated & (btnValue === employeeid)
                      ? newFirstname + " " + newLastname
                      : firstname + " " + "lastname"
                  }
                  type="text"
                  autoFocus
                  onKeyUp={(e) => {
                    setNewFirstnameUpdated(true);
                    setNewLastnameUpdated(true);
                    setCombinedName(e.target.value);
                    setNewFirstname(splitName[0]);
                    setNewLastname(splitName[1]);
                  }}
                  placeholder={
                    (btnValue === employeeid) & !newFirstnameUpdated
                      ? firstname + " " + lastname
                      : newFirstnameUpdated & (btnValue === employeeid)
                      ? newFirstname + " " + newLastname
                      : firstname + " " + "lastname"
                  }
                />
                <span className="form-control-container-icon_end">
                  <i className="fa-regular fa-user"></i>
                </span>
              </div>
            </Form.Group>
            <Form.Group
              className="mb-3"
              controlId="exampleForm.ControlTextarea1"
            >
              <Form.Label>Department</Form.Label>
              <div className="form-control-container">
                {" "}
                <Form.Control
                  name="department"
                  className="form-control-container-input"
                  defaultValue={
                    (btnValue === employeeid) & !newDepartmentUpdated
                      ? department
                      : newDepartmentUpdated & (btnValue === employeeid)
                      ? newDepartment
                      : department
                  }
                  type="text"
                  onKeyUp={(e) => {
                    setNewDepartment(e.target.value);
                    setNewDepartmentUpdated(true);
                  }}
                  placeholder={
                    (btnValue === employeeid) & !newDepartmentUpdated
                      ? department
                      : newDepartmentUpdated & (btnValue === employeeid)
                      ? newDepartment
                      : department
                  }
                />
                <span className="form-control-container-icon_end">
                  <i className="fa-solid fa-briefcase"></i>
                </span>
              </div>
            </Form.Group>
            <Form.Group
              className="mb-3"
              controlId="exampleForm.ControlTextarea1"
            >
              <Form.Label>Designation</Form.Label>
              <div className="form-control-container">
                <Form.Control
                  name="designation"
                  className="form-control-container-input"
                  defaultValue={
                    (btnValue === employeeid) & !newDesignationUpdated
                      ? designation
                      : newDesignationUpdated & (btnValue === employeeid)
                      ? newDesignation
                      : designation
                  }
                  type="text"
                  onKeyUp={(e) => {
                    setNewDesignation(e.target.value);
                    setNewDesignationUpdated(true);
                  }}
                  placeholder={
                    (btnValue === employeeid) & !newDesignationUpdated
                      ? designation
                      : newDesignationUpdated & (btnValue === employeeid)
                      ? newDesignation
                      : designation
                  }
                />
                <span className="form-control-container-icon_end">
                  <i className="fa-regular fa-flag"></i>
                </span>
              </div>
            </Form.Group>
            <Form.Group
              className="mb-3"
              controlId="exampleForm.ControlTextarea1"
            >
              <Form.Label>Mobile</Form.Label>
              <div className="form-control-container">
                <Form.Control
                  name="mobile"
                  className="form-control-container-input"
                  defaultValue={
                    (btnValue === employeeid) & !newMobileUpdated
                      ? mobile
                      : newMobileUpdated & (btnValue === employeeid)
                      ? newMobile
                      : mobile
                  }
                  type="number"
                  onKeyUp={(e) => {
                    setNewMobile(e.target.value);
                    setNewMobileUpdated(true);
                  }}
                  placeholder={
                    (btnValue === employeeid) & !newMobileUpdated
                      ? mobile
                      : newMobileUpdated & (btnValue === employeeid)
                      ? newMobile
                      : mobile
                  }
                />
                <span className="form-control-container-icon_end">
                  <i className="fa-solid fa-phone"></i>
                </span>
              </div>
            </Form.Group>
            <Form.Group
              className="mb-3"
              controlId="exampleForm.ControlTextarea1"
            >
              <Form.Label>Email</Form.Label>
              <div className="form-control-container">
                <Form.Control
                  name="email"
                  className="form-control-container-input"
                  defaultValue={
                    (btnValue === employeeid) & !newEmailUpdated
                      ? email
                      : newEmailUpdated & (btnValue === employeeid)
                      ? newEmail
                      : email
                  }
                  type="text"
                  onKeyUp={(e) => {
                    setNewEmail(e.target.value);
                    setNewEmailUpdated(true);
                  }}
                  placeholder={
                    (btnValue === employeeid) & !newEmailUpdated
                      ? email
                      : newEmailUpdated & (btnValue === employeeid)
                      ? newEmail
                      : email
                  }
                />
                <span className="form-control-container-icon_end">
                  <i className="fa-regular fa-envelope"></i>
                </span>
              </div>
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button
            className="close-btn"
            variant="secondary"
            onClick={handleClose}
          >
            Close
          </Button>
          <Button
            className="update-btn"
            variant="primary"
            onClick={(e) => {
              handleClose();
              setEditMode(!editMode);
              updateEmployee(e, employeeid);
              updateEmployee(e, employeeid);
            }}
          >
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default AllEmployees;
