import React, { useEffect, useState } from "react";
import Nav from "../Global/Nav";
import { Container, Button, Row, Form, Col } from "react-bootstrap";

const AllEmployees = () => {
  let dateObj = new Date();
  let year = dateObj.getFullYear();
  let month = dateObj.getMonth();
  let day = dateObj.getDate();

  const finalMonth = () => {
    if ((month <= 10) & (month === 0)) {
      return `0${month + 1}`;
    } else {
      return month;
    }
  };
  const finalDay = () => {
    if (day <= 10) {
      return `0${day}`;
    } else {
      return day;
    }
  };
  let date = `${year}-${finalMonth()}-${finalDay()}`;

  // get employees
  useEffect(() => {
    fetch(`${URL}/employees`)
      .then((res) => res.json())
      .then((json) => setEmployees(json));
  }, []);

  const URL = "http://localhost:8080";
  const [employees, setEmployees] = useState([]);

  // EDIT MODE
  const [editMode, setEditMode] = useState(false);

  // SET UPDATED INPUTS
  const [newDepartmentUpdated, setNewDepartmentUpdated] = useState(false);
  const [newDesignationUpdated, setNewDesignationUpdated] = useState(false);
  const [newEmailUpdated, setNewEmailUpdated] = useState(false);
  const [newFirstnameUpdated, setNewFirstnameUpdated] = useState(false);
  const [newLastnameUpdated, setNewLastnameUpdated] = useState(false);
  const [newMobileUpdated, setNewMobileUpdated] = useState(false);

  // SET NEW FORM
  const [newDepartment, setNewDepartment] = useState("");
  const [newDesignation, setNewDesignation] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newFirstname, setNewFirstname] = useState("");
  const [newLastname, setNewLastname] = useState("");
  const [newMobile, setNewMobile] = useState("");

  // EDIT  Employee
  function editEmployee() {
    setEditMode(!editMode);
  }

  // UPDATE EMPLOYEE
  async function updateEmployee(e, id) {
    e.preventDefault();
    setEditMode(!editMode);

    const foundEmployee = employees.filter((employee) => {
      const { employeeid } = employee;
      if (employeeid === id) {
        return employee;
      }
    });

    // Post options
    const options = {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        employeeid: foundEmployee[0].employeeid,
        fname: `${
          newFirstnameUpdated ? newFirstname : foundEmployee[0].firstname
        }`,
        lname: `${
          newLastnameUpdated ? newLastname : foundEmployee[0].lastname
        }`,
        mobile: `${newMobileUpdated ? newMobile : foundEmployee[0].mobile}`,
        designation: `${
          newDesignationUpdated ? newDesignation : foundEmployee[0].designation
        }`,
        department: `${
          newDepartmentUpdated ? newDepartment : foundEmployee[0].department
        }`,
        email: `${newEmailUpdated ? newEmail : foundEmployee[0].email}`,
      }),
    };

    try {
      const fetchPromiseResponse = await fetch(
        `${URL}/update-employee`,
        options
      );
      if (!fetchPromiseResponse.ok) {
        console.log(
          `Something went wrong with fetch from server ${fetchPromiseResponse.status}`
        );
      }
      const jsonPromiseResponse = fetchPromiseResponse.json();

      jsonPromiseResponse.then((res) => {
        console.log(res);
      });
    } catch {
      (err) => {
        console.log(`FETCH FAILED: ${err}`);
      };
    }
  }

  return (
    <>
      <Nav />

      <section className="all-employees">
        <div className="all-employees_header">
          <h4>Employees</h4>
        </div>
        {employees.map((employee) => {
          const {
            department,
            designation,
            email,
            employeeid,
            firstname,
            lastname,
            mobile,
          } = employee;

          return (
            <>
              <Form
                key={employeeid}
                onSubmit={(e) => updateEmployee(e, employeeid)}
                autoComplete="true"
                className="all-employees_form"
              >
                <Row className="justify-content-between">
                  <Col
                    lg="1"
                    style={{ width: "3%" }}
                  >
                    <Row style={{ "justify-content": "center" }}>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <input
                          style={{ margin: "0" }}
                          className="radio"
                          type="radio"
                          name=""
                          id=""
                        />
                      </Col>
                    </Row>
                  </Col>
                  <Col lg="1">
                    <Row>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <p>Image</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <img
                          src=""
                          alt="picturrrrrr"
                        />
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <p>Employee ID: </p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <p> {employeeid} </p>
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <p>Name: </p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <input
                            type="text"
                            name=""
                            onChange={(e) => {
                              setNewFirstname(e.target.value);
                              setNewFirstnameUpdated(true);
                            }}
                            placeholder={
                              editMode & !newFirstnameUpdated
                                ? firstname
                                : newFirstnameUpdated & editMode
                                ? newFirstname
                                : firstname
                            }
                          />
                        ) : (
                          <p>
                            {" "}
                            {newFirstnameUpdated
                              ? newFirstname
                              : firstname}{" "}
                          </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <p>Department </p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <input
                            type="text"
                            onChange={(e) => {
                              setNewDepartment(e.target.value);
                              setNewDepartmentUpdated(true);
                            }}
                            placeholder={
                              editMode & !newDepartmentUpdated
                                ? department
                                : newDepartmentUpdated & editMode
                                ? newDepartment
                                : department
                            }
                          />
                        ) : (
                          <p>
                            {" "}
                            {newDepartmentUpdated
                              ? newDepartment
                              : department}{" "}
                          </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <p>Role</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <input
                            type="text"
                            onChange={(e) => {
                              setNewDesignation(e.target.value);
                              setNewDesignationUpdated(true);
                            }}
                            placeholder={
                              editMode & !newDesignationUpdated
                                ? designation
                                : newDesignationUpdated & editMode
                                ? newDesignation
                                : designation
                            }
                          />
                        ) : (
                          <p>
                            {newDesignationUpdated
                              ? newDesignation
                              : designation}
                          </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <p>Mobile</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <input
                            type="text"
                            onChange={(e) => {
                              setNewMobile(e.target.value);
                              setNewMobileUpdated(true);
                            }}
                            placeholder={
                              editMode & !newMobileUpdated
                                ? mobile
                                : newMobileUpdated & editMode
                                ? newMobile
                                : mobile
                            }
                          />
                        ) : (
                          <p> {newMobileUpdated ? newMobile : mobile} </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <p>email</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <input
                            type="text"
                            onChange={(e) => {
                              setNewEmail(e.target.value);
                              setNewEmailUpdated(true);
                            }}
                            placeholder={
                              editMode & !newEmailUpdated
                                ? email
                                : newEmailUpdated & editMode
                                ? newEmail
                                : email
                            }
                          />
                        ) : (
                          <p> {newEmailUpdated ? newEmail : email} </p>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <p>join date</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        <p> {date} </p>
                      </Col>
                    </Row>
                  </Col>

                  <Col lg="1">
                    <Row>
                      <Col
                        lg="12"
                        className="col-6 d-none d-lg-block"
                      >
                        <p>Actions</p>
                      </Col>
                      <Col
                        lg="12"
                        className="col-6"
                      >
                        {editMode ? (
                          <Button type="submit">UPDATE</Button>
                        ) : (
                          <div className="form-btns">
                            <Button
                              onClick={editEmployee}
                              type="button"
                            >
                              Edit
                            </Button>

                            <Button type="button">delete</Button>
                          </div>
                        )}
                      </Col>
                    </Row>
                  </Col>

                  {/* END CONTAINER ROW */}
                </Row>
              </Form>
              <hr />;
            </>
          );
        })}
      </section>
    </>
  );
};

export default AllEmployees;
