import React, { useEffect, useState } from "react";
import Nav from "../Global/Nav";

import EmployeeCard from "./EmployeeCard";

const AllEmployees = () => {
  let dateObj = new Date();
  let year = dateObj.getFullYear();
  let month = dateObj.getMonth();
  let day = dateObj.getDate();

  const finalMonth = () => {
    if ((month <= 10) & (month === 0)) {
      return `0${month + 1}`;
    } else {
      return month;
    }
  };
  const finalDay = () => {
    if (day <= 10) {
      return `0${day}`;
    } else {
      return day;
    }
  };
  let date = `${year}-${finalMonth()}-${finalDay()}`;

  

  return (
    <>
      <Nav />

      <section className="all-employees">
        <div className="all-employees_header">
          <h4>Employees</h4>
        </div>
        {employees.map((employee) => {
          const {
            department,
            designation,
            email,
            employeeid,
            firstname,
            lastname,
            mobile,
          } = employee;

          return (
            <EmployeeCard
              key={employeeid}
              editMode={editMode}
              btnValue={btnValue}
              employeeid={employeeid}
              firstname={firstname}
              department={department}
              designation={designation}
              mobile={mobile}
              email={email}
              date={date}
              newFirstnameUpdated={newFirstnameUpdated}
              newDepartmentUpdated={newDepartmentUpdated}
              newDesignationUpdated={newDesignationUpdated}
              newMobileUpdated={newMobileUpdated}
              newEmailUpdated={newEmailUpdated}

            />
          );
        })}
      </section>
    </>
  );
};

export default AllEmployees;
