import React, { useEffect, useState } from "react";
import Nav from "../Global/Nav";
import ProjectCard from "./ProjectCard";
import Container from "react-bootstrap/esm/Container";
import { Row, Col } from "react-bootstrap";

const AllProjects = () => {
  // get Project
  useEffect(() => {
    fetch(`${URL}/projects`)
      .then((res) => res.json())
      .then((json) => setProjects(json));
  }, []);

  const URL = "http://localhost:8080";
  const [projects, setProjects] = useState([]);

  return (
    <>
      <Nav />

      <Container>
        {" "}
        <section className="project">
          <div></div>
          <h2>New Projects</h2>
          <p>3 projects</p>
          <Row>
            {projects.map((project) => {
              const {
                projectID,
                title,
                department,
                priority,
                client,
                price,
                startDate,
                endDate,
                team,
                status,
                description,
              } = project;

              return (
                <Col
                  key={projectID}
                  md="6"
                >
                  {" "}
                  <ProjectCard
                    URL={URL}
                    projectID={projectID}
                    title={title}
                    department={department}
                    priority={priority}
                    client={client}
                    price={price}
                    startDate={startDate}
                    endDate={endDate}
                    team={team}
                    status={status}
                    description={description}
                  />
                </Col>
              );
            })}
          </Row>
        </section>
      </Container>
    </>
  );
};

export default AllProjects;
