import { useEffect, useState } from "react";

const PostEmployee = (employeeInitialProps) => {
  const employeeFinalProps = {
    fname: employeeInitialProps.    fname,
    lname: employeeInitialProps.    lname,
    gender: employeeInitialProps.   gender,
    mobile: employeeInitialProps.   mobile,
    password: employeeInitialProps. password,
    designation: employeeInitialProps.  designation,
    department: employeeInitialProps.   department,
    address: employeeInitialProps.  address,
    email: employeeInitialProps.    email,
    dateofbirth: employeeInitialProps.  dob,
    degree: employeeInitialProps.   degree,
  };
  // ADD EMPLOYEE TO DB
  async function addEmployeeNow(employeeInitialProps) {
    // Post options
    const options = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(employeeFinalProps),
    };

    try {
      const fetchPromiseResponse = await fetch(`${URL}/add-employee`, options);
      if (!fetchPromiseResponse.ok) {
        console.log(
          `Something went wrong with fetch from server ${fetchPromiseResponse.status}`
        );
      }
      const jsonPromiseResponse = fetchPromiseResponse.json();

      jsonPromiseResponse.then((res) => {
        console.log(res);
      });
    } catch {
      (err) => {
        console.log(`FETCH FAILED: ${err}`);
      };
    }
  }
};

export default PostEmployee;
