import React, { useEffect, useState } from "react";
import Nav from "../Global/Nav";

const AllEmployees = () => {
  let dateObj = new Date();
  let year = dateObj.getFullYear();
  let month = dateObj.getMonth();
  let day = dateObj.getDate();

  const finalMonth = () => {
    if ((month <= 10) & (month === 0)) {
      return `0${month + 1}`;
    } else {
      return month;
    }
  };
  const finalDay = () => {
    if (day <= 10) {
      return `0${day}`;
    } else {
      return day;
    }
  };
  let date = `${year}-${finalMonth()}-${finalDay()}`;

  // get employees
  useEffect(() => {
    fetch(`${URL}/employees`)
      .then((res) => res.json())
      .then((json) => setEmployees(json));
  }, []);

  const URL = "http://localhost:8080";
  const [employees, setEmployees] = useState([]);

  // EDIT MODE
  const [editMode, setEditMode] = useState(false);
  // SET UPDATED INPUTS
  const [newAddressUpdated, setNewAddressUpdated] = useState(false);
  const [newDateofbirthUpdated, setNewDateofbirthUpdated] = useState(false);
  const [newDegreeUpdated, setNewDegreeUpdated] = useState(false);
  const [newDepartmentUpdated, setNewDepartmentUpdated] = useState(false);
  const [newDesignationUpdated, setNewDesignationUpdated] = useState(false);
  const [newEmailUpdated, setNewEmailUpdated] = useState(false);
  const [newFirstnameUpdated, setNewFirstnameUpdated] = useState(false);
  const [newGenderUpdated, setNewGenderUpdated] = useState(false);
  const [newLastnameUpdated, setNewLastnameUpdated] = useState(false);
  const [newMobileUpdated, setNewMobileUpdated] = useState(false);
  // SET NEW FORM
  const [newAddress, setNewAddress] = useState("");
  const [newDateofbirth, setNewDateofbirth] = useState("");
  const [newDegree, setNewDegree] = useState("");
  const [newDepartment, setNewDepartment] = useState("");
  const [newDesignation, setNewDesignation] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newFirstname, setNewFirstname] = useState("");
  const [newGender, setNewGender] = useState("");
  const [newLastname, setNewLastname] = useState("");
  const [newMobile, setNewMobile] = useState("");

  // UPDATED

  function editEmployee() {
    setEditMode(!editMode);
  }
  console.log(`EDIT MODE: ${editMode}`);
  console.log(newGender);

  function updateEmployee(e) {
    e.preventDefault();
    setEditMode(!editMode);
  }

  return (
    <>
      <Nav />
      <h1>ALL EMPLOYEES</h1>
      {employees.map((employee) => {
        const {
          address,
          dateofbirth,
          degree,
          department,
          designation,
          email,
          employeeid,
          firstname,
          gender,
          lastname,
          mobile,
        } = employee;

        return (
          <section
            className="employee-card"
            key={employeeid}
          >
            <input
              type="radio"
              name=""
              id=""
            />
            <form
              onSubmit={(e) => updateEmployee(e)}
              autoComplete="true"
            >
              <div style={{ display: "flex" }}>
                <p>Image</p>
                <img
                  src=""
                  alt="picturrrrrr"
                />
              </div>
              <div style={{ display: "flex" }}>
                <p>Employee ID: </p>
                <p> {employeeid} </p>
              </div>
              <div style={{ display: "flex" }}>
                <p>Gender: </p>
                {editMode ? (
                  <>
                    <input
                      type="text"
                      name=""
                      id=""
                      onChange={(e) => {
                        setNewGender(e.target.value);
                        setNewGenderUpdated(true);
                      }}
                      placeholder={
                        editMode & !newGenderUpdated
                          ? gender
                          : newGenderUpdated & editMode
                          ? newGender
                          : gender
                      }
                    />
                  </>
                ) : (
                  <p> {newGenderUpdated ? newGender : gender} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Name: </p>
                {editMode ? (
                  <input
                    type="text"
                    name=""
                    onChange={(e) => {
                      setNewFirstname(e.target.value);
                      setNewFirstnameUpdated(true);
                    }}
                    placeholder={
                      editMode & !newFirstnameUpdated
                        ? firstname
                        : newFirstnameUpdated & editMode
                        ? newFirstname
                        : firstname
                    }
                  />
                ) : (
                  <p> {newFirstnameUpdated ? newFirstname : firstname} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Last Name: </p>
                {editMode ? (
                  <input
                    type="text"
                    name=""
                    onChange={(e) => {
                      setNewLastname(e.target.value);
                      setNewLastnameUpdated(true);
                    }}
                    placeholder={
                      editMode & !newLastnameUpdated
                        ? lastname
                        : newLastnameUpdated & editMode
                        ? newLastname
                        : lastname
                    }
                  />
                ) : (
                  <p> {newLastnameUpdated ? newLastname : lastname} </p>
                )}
              </div>

              <div style={{ display: "flex" }}>
                <p>DOB</p>

                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewDateofbirth(e.target.value);
                      setNewDateofbirthUpdated(true);
                    }}
                    placeholder={
                      editMode & !newDateofbirthUpdated
                        ? dateofbirth
                        : newDateofbirthUpdated & editMode
                        ? newDateofbirth
                        : dateofbirth
                    }
                  />
                ) : (
                  <p> {dateofbirth} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Department </p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewDepartment(e.target.value);
                      setNewDepartmentUpdated(true);
                    }}
                    placeholder={
                      editMode & !newDateofbirthUpdated
                        ? department
                        : newDateofbirthUpdated & editMode
                        ? newDepartment
                        : department
                    }
                  />
                ) : (
                  <p> {newDateofbirthUpdated ? newDepartment : department} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Role</p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewDesignation(e.target.value);
                      setNewDesignationUpdated(true);
                    }}
                    placeholder={
                      editMode & !newDesignationUpdated
                        ? designation
                        : newDesignationUpdated & editMode
                        ? newDesignation
                        : designation
                    }
                  />
                ) : (
                  <p>
                    {" "}
                    {newDesignationUpdated ? newDesignation : designation}{" "}
                  </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Degree</p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewDegree(e.target.value);
                      setNewDegreeUpdated(true);
                    }}
                    placeholder={
                      editMode & !newDegreeUpdated
                        ? degree
                        : newDegreeUpdated & editMode
                        ? newDegree
                        : degree
                    }
                  />
                ) : (
                  <p> {newDegreeUpdated ? newDegree : degree} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Mobile</p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewMobile(e.target.value);
                      setNewMobileUpdated(true);
                    }}
                    placeholder={
                      editMode & !newMobileUpdated
                        ? degree
                        : newMobileUpdated & editMode
                        ? newDegree
                        : degree
                    }
                  />
                ) : (
                  <p> {newMobileUpdated ? newMobile : mobile} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>email</p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewEmail(e.target.value);
                      setNewEmailUpdated(true);
                    }}
                    placeholder={
                      editMode & !newEmailUpdated
                        ? email
                        : newEmailUpdated & editMode
                        ? newEmail
                        : email
                    }
                  />
                ) : (
                  <p> {newEmailUpdated ? newEmail : email} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Address</p>

                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewAddress(e.target.value);
                      setNewAddressUpdated(true);
                    }}
                    placeholder={
                      editMode & !newAddressUpdated
                        ? address
                        : newAddressUpdated & editMode
                        ? newAddress
                        : address
                    }
                  />
                ) : (
                  <p> {newAddressUpdated ? newAddress : address} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>join date</p>
                <p> {date} </p>
              </div>
              {editMode ? (
                <button type="submit">UPDATE</button>
              ) : (
                <>
                  <button
                    onClick={editEmployee}
                    type="button"
                  >
                    Edit
                  </button>

                  <button type="button">delete</button>
                </>
              )}
            </form>
          </section>
        );
      })}
    </>
  );
};

export default AllEmployees;
