import React, { useEffect, useState } from "react";
import Nav from "../Global/Nav";

const AllEmployees = () => {
  let dateObj = new Date();
  let year = dateObj.getFullYear();
  let month = dateObj.getMonth();
  let day = dateObj.getDate();

  const finalMonth = () => {
    if ((month <= 10) & (month === 0)) {
      return `0${month + 1}`;
    } else {
      return month;
    }
  };
  const finalDay = () => {
    if (day <= 10) {
      return `0${day}`;
    } else {
      return day;
    }
  };
  let date = `${year}-${finalMonth()}-${finalDay()}`;

  // get employees
  useEffect(() => {
    fetch(`${URL}/employees`)
      .then((res) => res.json())
      .then((json) => setEmployees(json));
  }, []);

  const URL = "http://localhost:8080";
  const [employees, setEmployees] = useState([]);

  // EDIT MODE
  const [editMode, setEditMode] = useState(false);
  // SET FORM
  const [newAddress, setNewAddress] = useState("");
  const [newDateofbirth, setNewDateofbirth] = useState("");
  const [newDegree, setNewDegree] = useState("");
  const [newDepartment, setNewDepartment] = useState("");
  const [newDesignation, setNewDesignation] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newFirstname, setNewFirstname] = useState("");
  const [newGender, setNewGender] = useState("");
  const [newLastname, setNewLastname] = useState("");
  const [newMobile, setNewMobile] = useState("");

  // UPDATED
  const [updated, setUpdated] = useState(false);

  function editEmployee() {
    setEditMode(!editMode);
  }
  console.log(`EDIT MODE: ${editMode}`);
  console.log(`FORM WAS UPDATED:: ${updated}`);
  console.log(newGender);

  function updateEmployee(e) {
    e.preventDefault();
    setEditMode(!editMode);
    console.log(`UPDATED Was Reset:: ${updated}`);
  }

  return (
    <>
      <Nav />
      <h1>ALL EMPLOYEES</h1>
      {employees.map((employee) => {
        const {
          address,
          dateofbirth,
          degree,
          department,
          designation,
          email,
          employeeid,
          firstname,
          gender,
          lastname,
          mobile,
        } = employee;

        return (
          <section
            className="employee-card"
            key={employeeid}
          >
            <input
              type="radio"
              name=""
              id=""
            />
            <form
              onSubmit={(e) => updateEmployee(e)}
              autoComplete="true"
            >
              <div style={{ display: "flex" }}>
                <p>Image</p>
                <img
                  src=""
                  alt="picturrrrrr"
                />
              </div>
              <div style={{ display: "flex" }}>
                <p>Employee ID: </p>
                <p> {employeeid} </p>
              </div>
              <div style={{ display: "flex" }}>
                <p>Gender: </p>
                {editMode ? (
                  <>
                    <input
                      type="text"
                      name=""
                      id=""
                      onChange={(e) => {
                        setUpdated(true);
                        setNewGender(e.target.value);
                      }}
                      placeholder={
                        editMode & !updated
                          ? gender
                          : updated & editMode
                          ? newGender
                          : gender
                      }
                    />
                  </>
                ) : (
                  <p> {updated ? newGender : gender} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Name: </p>
                {editMode ? (
                  <input
                    type="text"
                    name=""
                    onChange={(e) => {
                      setUpdated(true);
                      setNewFirstname(e.target.value);
                    }}
                    placeholder={
                      editMode & !updated
                        ? firstname
                        : updated & editMode
                        ? newFirstname
                        : firstname
                    }
                  />
                ) : (
                  <p> {updated ? newFirstname : firstname} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Last Name: </p>
                {editMode ? (
                  <input
                    type="text"
                    name=""
                    onChange={(e) => {
                      setUpdated(true);
                      setNewLastname(e.target.value);
                    }}
                    placeholder={
                      editMode & !updated
                        ? lastname
                        : updated & editMode
                        ? newLastname
                        : lastname
                    }
                  />
                ) : (
                  <p> {updated ? newLastname : lastname} </p>
                )}
              </div>

              <div style={{ display: "flex" }}>
                <p>DOB</p>

                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setUpdated(true);
                      setNewDateofbirth(e.target.value);
                    }}
                    placeholder={
                      editMode & !updated
                        ? dateofbirth
                        : updated & editMode
                        ? newDateofbirth
                        : dateofbirth
                    }
                  />
                ) : (
                  <p> {dateofbirth} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Department </p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setUpdated(true);
                      setNewDepartment(e.target.value);
                    }}
                    placeholder={
                      editMode & !updated
                        ? department
                        : updated & editMode
                        ? newDepartment
                        : department
                    }
                  />
                ) : (
                  <p> {updated ? newDepartment : department} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Role</p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setUpdated(true);
                      setNewDesignation(e.target.value);
                    }}
                    placeholder={
                      editMode & !updated
                        ? designation
                        : updated & editMode
                        ? newDesignation
                        : designation
                    }
                  />
                ) : (
                  <p> {updated ? newDesignation : designation} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Degree</p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setUpdated(true);
                      setNewDegree(e.target.value);
                    }}
                    placeholder={
                      editMode & !updated
                        ? degree
                        : updated & editMode
                        ? newDegree
                        : degree
                    }
                  />
                ) : (
                  <p> {updated ? newDegree : degree} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Mobile</p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setUpdated(true);
                      setNewMobile(e.target.value);
                    }}
                    placeholder={editMode ? mobile : newMobile}
                  />
                ) : (
                  <p> {updated ? newMobile : mobile} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>email</p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setUpdated(true);
                      setNewEmail(e.target.value);
                    }}
                    placeholder={editMode ? email : newEmail}
                  />
                ) : (
                  <p> {updated ? newEmail : email} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Address</p>

                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setUpdated(true);
                      setNewAddress(e.target.value);
                    }}
                    placeholder={editMode ? address : newAddress}
                  />
                ) : (
                  <p> {updated ? newAddress : address} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>join date</p>
                <p> {date} </p>
              </div>
              {editMode ? (
                <button type="submit">UPDATE</button>
              ) : (
                <>
                  <button
                    onClick={editEmployee}
                    type="button"
                  >
                    Edit
                  </button>

                  <button type="button">delete</button>
                </>
              )}
            </form>
          </section>
        );
      })}
    </>
  );
};

export default AllEmployees;
