import React, { useEffect, useState } from "react";
import Nav from "../Global/Nav";
import { Container, Form } from "react-bootstrap";

import EmployeeCard from "./EmployeeCard";

const AllEmployees = () => {
  let dateObj = new Date();
  let year = dateObj.getFullYear();
  let month = dateObj.getMonth();
  let day = dateObj.getDate();

  const finalMonth = () => {
    if ((month <= 10) & (month === 0)) {
      return `0${month + 1}`;
    } else {
      return month;
    }
  };

  const finalDay = () => {
    if (day <= 10) {
      return `0${day}`;
    } else {
      return day;
    }
  };
  let date = `${year}-${finalMonth()}-${finalDay()}`;

  const URL = "http://localhost:8080";
  // get employees
  useEffect(() => {
    fetch(`${URL}/employees`)
      .then((res) => res.json())
      .then((json) => setEmployees(json));
  }, []);
  const [employees, setEmployees] = useState([]);

  let filteredEmloyees = [];
  const [employeeNewArray, setemployeeNewArray] = useState([]);

  console.log(employeeNewArray);

  return (
    <>
      <Nav />

      <Container>
        <section className="employees">
          <div className="employees-header">
            <h2>Employees</h2>
            <Form.Control
              className=''
              type="text"
              placeholder="Search"
              onChange={(e) => {
                filteredEmloyees = employees.filter((employee) => {
                  const {
                    firstname,
                    lastname,
                    department,
                    employeeid,
                    designation,
                    email,
                    date,
                    mobile,
                  } = employee;
                  return (
                    firstname == e.target.value ||
                    lastname == e.target.value ||
                    department == e.target.value ||
                    employeeid == e.target.value ||
                    designation == e.target.value ||
                    department == e.target.value ||
                    email == e.target.value ||
                    date == e.target.value ||
                    mobile == e.target.value
                  );
                });
                setemployeeNewArray(filteredEmloyees);
              }}
            />
          </div>
          {employeeNewArray.length > 0
            ? employeeNewArray.map((employee) => {
                const {
                  department,
                  designation,
                  email,
                  employeeid,
                  firstname,
                  lastname,
                  mobile,
                } = employee;

                return (
                  <EmployeeCard
                    URL={URL}
                    employees={employees}
                    key={employeeid}
                    employeeid={employeeid}
                    firstname={firstname}
                    lastname={lastname}
                    department={department}
                    designation={designation}
                    mobile={mobile}
                    email={email}
                    date={date}
                  />
                );
              })
            : employees.map((employee) => {
                const {
                  department,
                  designation,
                  email,
                  employeeid,
                  firstname,
                  lastname,
                  mobile,
                } = employee;

                return (
                  <EmployeeCard
                    URL={URL}
                    employees={employees}
                    key={employeeid}
                    employeeid={employeeid}
                    firstname={firstname}
                    lastname={lastname}
                    department={department}
                    designation={designation}
                    mobile={mobile}
                    email={email}
                    date={date}
                  />
                );
              })}
        </section>
      </Container>
    </>
  );
};

export default AllEmployees;
