import { Container, Button, Row, Form, Col } from "react-bootstrap";
import React, { useEffect, useState } from "react";

const EmployeeCard = ({
  URL,
  employees,
  employeeid,
  firstname,
  lastname,
  department,
  designation,
  mobile,
  email,

  date,
}) => {
  // EDIT MODE
  const [editMode, setEditMode] = useState(false);
  const [btnValue, setBtnValue] = useState(0);
  const [employee, setEmployee] = useState("");

  const findEmployee = (id) => {
    let foundEmployee = employees.filter((employee) => {
      const { employeeid } = employee;
      if (employeeid === id) {
        return employee;
      }
    });
    setBtnValue(id);
    setEditMode(!editMode);
    setEmployee(foundEmployee);
    return foundEmployee;
  };
  // SET UPDATED INPUTS
  const [newDepartmentUpdated, setNewDepartmentUpdated] = useState(false);
  const [newDesignationUpdated, setNewDesignationUpdated] = useState(false);
  const [newEmailUpdated, setNewEmailUpdated] = useState(false);
  const [newFirstnameUpdated, setNewFirstnameUpdated] = useState(false);
  const [newLastnameUpdated, setNewLastnameUpdated] = useState(false);
  const [newMobileUpdated, setNewMobileUpdated] = useState(false);

  // SET NEW FORM
  const [newDepartment, setNewDepartment] = useState("");
  const [newDesignation, setNewDesignation] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newFirstname, setNewFirstname] = useState("");
  const [newLastname, setNewLastname] = useState("");
  const [newMobile, setNewMobile] = useState( "" );
  const [combinedName, setCombinedName] = useState( "" );
  let splitName = combinedName.
  console.log(combinedName)
  

  // UPDATE EMPLOYEE
  async function updateEmployee(e, id) {
    e.preventDefault();

    // Post options
    const options = {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        employeeid: employee[0].employeeid,
        fname: `${newFirstnameUpdated ? newFirstname : employee[0].firstname}`,
        lname: `${newLastnameUpdated ? newLastname : employee[0].lastname}`,
        mobile: `${newMobileUpdated ? newMobile : employee[0].mobile}`,
        designation: `${
          newDesignationUpdated ? newDesignation : employee[0].designation
        }`,
        department: `${
          newDepartmentUpdated ? newDepartment : employee[0].department
        }`,
        email: `${newEmailUpdated ? newEmail : employee[0].email}`,
      }),
    };

    try {
      const fetchPromiseResponse = await fetch(
        `${URL}/update-employee`,
        options
      );
      if (!fetchPromiseResponse.ok) {
        console.log(
          `Something went wrong with fetch from server ${fetchPromiseResponse.status}`
        );
      }
      const jsonPromiseResponse = fetchPromiseResponse.json();

      jsonPromiseResponse.then((res) => {
        console.log(res);
      });
    } catch {
      (err) => {
        console.log(`FETCH FAILED: ${err}`);
      };
    }
  }

  return (
    <section>
      <Form
        onSubmit={(e) => updateEmployee(e, employeeid, findEmployee)}
        autoComplete="true"
        className="all-employees_form"
      >
        <Row>
          <Col
            lg="1"
            style={{ width: "3%" }}
          >
            <Row style={{ justifyContent: "center" }}>
              <Col
                lg="12"
                className="col-6"
              >
                <input
                  style={{ margin: "0" }}
                  className="radio"
                  type="radio"
                  name=""
                  id=""
                />
              </Col>
            </Row>
          </Col>
          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>Image</p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                <img
                  src=""
                  alt="picturrrrrr"
                />
              </Col>
            </Row>
          </Col>

          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>Employee ID: </p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                <p> {employeeid} </p>
              </Col>
            </Row>
          </Col>

          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>Name: </p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                {(btnValue === employeeid) & (editMode == true) ? (
                  <input
                    type="text"
                    name=""
                    onChange={(e) => {
                      setNewFirstname(e.target.value);
                      setNewFirstnameUpdated( true );
                      setCombinedName(e.target.value)
                      console.log(e.target.value);
                    }}
                    placeholder={
                      (btnValue === employeeid) & !newFirstnameUpdated
                        ? firstname
                        : newFirstnameUpdated & (btnValue === employeeid)
                        ? newFirstname
                        : firstname
                    }
                  />
                ) : (
                  <p> {newFirstnameUpdated ? newFirstname : firstname} </p>
                )}
              </Col>
            </Row>
          </Col>

          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>Department </p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                {(btnValue === employeeid) & (editMode == true) ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewDepartment(e.target.value);
                      setNewDepartmentUpdated(true);
                    }}
                    placeholder={
                      (btnValue === employeeid) & !newDepartmentUpdated
                        ? department
                        : newDepartmentUpdated & (btnValue === employeeid)
                        ? newDepartment
                        : department
                    }
                  />
                ) : (
                  <p>{newDepartmentUpdated ? newDepartment : department}</p>
                )}
              </Col>
            </Row>
          </Col>

          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>Role</p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                {(btnValue === employeeid) & (editMode == true) ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewDesignation(e.target.value);
                      setNewDesignationUpdated(true);
                    }}
                    placeholder={
                      (btnValue === employeeid) & !newDesignationUpdated
                        ? designation
                        : newDesignationUpdated & (btnValue === employeeid)
                        ? newDesignation
                        : designation
                    }
                  />
                ) : (
                  <p>{newDesignationUpdated ? newDesignation : designation}</p>
                )}
              </Col>
            </Row>
          </Col>

          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>Mobile</p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                {(btnValue === employeeid) & (editMode == true) ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewMobile(e.target.value);
                      setNewMobileUpdated(true);
                    }}
                    placeholder={
                      (btnValue === employeeid) & !newMobileUpdated
                        ? mobile
                        : newMobileUpdated & (btnValue === employeeid)
                        ? newMobile
                        : mobile
                    }
                  />
                ) : (
                  <p> {newMobileUpdated ? newMobile : mobile} </p>
                )}
              </Col>
            </Row>
          </Col>

          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>email</p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                {(btnValue === employeeid) & (editMode == true) ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewEmail(e.target.value);
                      setNewEmailUpdated(true);
                    }}
                    placeholder={
                      (btnValue === employeeid) & !newEmailUpdated
                        ? email
                        : newEmailUpdated & (btnValue === employeeid)
                        ? newEmail
                        : email
                    }
                  />
                ) : (
                  <p> {newEmailUpdated ? newEmail : email} </p>
                )}
              </Col>
            </Row>
          </Col>

          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>join date</p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                <p> {date} </p>
              </Col>
            </Row>
          </Col>

          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6 d-none d-lg-block"
              >
                <p>Actions</p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                {(btnValue === employeeid) & (editMode == true) ? (
                  <Button
                    onClick={(e) => {
                      setEditMode(!editMode);
                      updateEmployee(e, employeeid);
                    }}
                    type="submit"
                  >
                    UPDATE
                  </Button>
                ) : (
                  <div className="form-btns">
                    <Button
                      onClick={(e) => {
                        findEmployee(employeeid);
                      }}
                      type="button"
                    >
                      Edit
                    </Button>

                    <Button type="button">delete</Button>
                  </div>
                )}
              </Col>
            </Row>
          </Col>

          {/* END CONTAINER ROW */}
        </Row>
      </Form>
      <hr />;
    </section>
  );
};

export default EmployeeCard;
