import React, { useState, useEffect } from "react";
import Nav from "../Global/Nav";
import AllEmployees from "../Employees/AllEmployees";
import start from "../../Hooks/getEmployees";

const AddEmployee = () => {
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [gender, setGender] = useState("");
  const [mobile, setMobile] = useState(0);
  const [password, setPassword] = useState("");
  const [designation, setDesignation] = useState("");
  const [department, setDepartment] = useState("");
  const [address, setAddress] = useState("");
  const [email, setEmail] = useState("");
  const [dob, setDOB] = useState("");
  const [degree, setDegree] = useState("");
  const URL = "http://localhost:8080";

  // ADD EMPLOYEE TO DB
  async function addEmployeeNow(e) {
    e.preventDefault();

    // Post options
    const options = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        fname: fname,
        lname: lname,
        gender: gender,
        mobile: mobile,
        password: password,
        designation: designation,
        department: department,
        address: address,
        email: email,
        dateofbirth: dob,
        degree: degree,
      }),
    };

    try {
      const fetchPromiseResponse = await fetch(`${URL}/add-employee`, options);
      if (!fetchPromiseResponse.ok) {
        console.log(
          `Something went wrong with fetch from server ${fetchPromiseResponse.status}`
        );
      }
      const jsonPromiseResponse = fetchPromiseResponse.json();

      jsonPromiseResponse.then((res) => {
        console.log(res);
      });
    } catch {
      (err) => {
        console.log(`FETCH FAILED: ${err}`);
      };
    }
  }

  // get employees
  useEffect(() => {
    async function getEmployees() {
      const URL = `http://localhost:8080`;
      try {
        const fetchPromisResponse = await fetch(`${URL}/employees`);
        if (!fetchPromisResponse.ok) {
          console.log(
            `Something went wrong with fetch from server ${fetchPromisResponse.status}`
          );
        }

        jsonPromiseResponse = await fetchPromisResponse.json();

        jsonPromiseResponse.then((res) => {
          console.log(res);
        });
      } catch {
        (err) => {
          console.log(`FETCH FAILED: ${err}`);
        };
      }
    }
    getEmployees
  }, []);

  // ADD EMPLOYEE TO Employee page
  // function createEmployeeSection() {
  // }

  return (
    <>
      <Nav />
      <form onSubmit={addEmployeeNow}>
        <label htmlFor="">Add Employee</label>
        <input
          autoComplete="name"
          type="text"
          placeholder="first name *"
          onChange={(e) => {
            setFname(e.target.value);
          }}
          required={true}
        />
        <input
          autoComplete="name"
          type="text"
          placeholder="last name"
          onChange={(e) => {
            setLname(e.target.value);
          }}
        />

        <input
          autoComplete="name"
          type="text"
          placeholder="gender"
          onChange={(e) => {
            setGender(e.target.value);
          }}
        />

        <input
          type="tel"
          placeholder="mobile*"
          onChange={(e) => {
            setMobile(e.target.value);
          }}
          required={true}
        />
        <input
          autoComplete="name"
          type="text"
          placeholder="password*"
          onChange={(e) => {
            setPassword(e.target.value);
          }}
          required={true}
        />
        <input
          autoComplete="name"
          type="text"
          placeholder="re-enter password*"
          onChange={(e) => {
            setFname(e.target.value);
          }}
          required={true}
        />
        <input
          autoComplete="name"
          type="text"
          placeholder="designmation"
          onChange={(e) => {
            setDesignation(e.target.value);
          }}
        />
        <br />
        <br />

        <label htmlFor="select department">Select Department</label>

        <select
          name="select department"
          id="select department"
          placeholder="Select Department"
          onChange={(e) => {
            setDepartment(e.target.value);
          }}
          required={true}
        >
          <option value="development">Development</option>
          <option
            selected
            value="designing"
          >
            designing
          </option>
          <option value="testing">testing</option>
          <option value="hr">HR</option>
        </select>

        <br />
        <br />
        <input
          autoComplete="name"
          type="text"
          placeholder="address"
          onChange={(e) => {
            setAddress(e.target.value);
          }}
        />

        <input
          autoComplete="name"
          type="text"
          placeholder="email"
          onChange={(e) => {
            setEmail(e.target.value);
          }}
        />

        <input
          type="date"
          placeholder="date of birth"
          onChange={(e) => {
            setDOB(e.target.value);
          }}
        />

        <input
          autoComplete="name"
          type="text"
          placeholder="Degree"
          onChange={(e) => {
            setDegree(e.target.value);
          }}
        />

        <p>upload image</p>
        <input type="file" />
        <button type="submit">Submit</button>
        <button type="button">Cancel</button>
      </form>
    </>
  );
};

export default AddEmployee;
