    e,
    address,
    dateofbirth,
    degree,
    department,
    designation,
    email,
    id,
    firstname,
    gender,
    lastname,
    mobile
  ) {
    e.preventDefault();
    setEditMode(!editMode);

    const foundEmployee = employees.filter((employee) => {
      const {
        employeeid,
        address,
        dateofbirth,
        degree,
        department,
        designation,
        email,
        firstname,
        gender,
        lastname,
        mobile,
      } = employee;
      if (employeeid === id) {
        return employee;
      }
    });

    console.log(foundEmployee);
    // Post options
    // const options = {
    //   method: "PUT",
    //   headers: { "Content-Type": "application/json" },
    //   body: JSON.stringify({
    //     employeeid: foundEmployee.employeeid,
    //     fname: `${
    //       newFirstnameUpdated ? newFirstname : foundEmployee.firstname
    //     }`,
    //     lname: `${newLastnameUpdated ? newLastname : foundEmployee.lastname}`,
    //     gender: `${newGenderUpdated ? newGender : foundEmployee.gender}`,
    //     mobile: `${newMobileUpdated ? newMobile : foundEmployee.mobile}`,
    //     designation: `${
    //       newDesignationUpdated ? newDesignation : foundEmployee.designation
    //     }`,
    //     department: `${
    //       newDepartmentUpdated ? newDepartment : foundEmployee.department
    //     }`,
    //     address: `${newAddressUpdated ? newAddress : foundEmployee.address}`,
    //     email: `${newEmailUpdated ? newEmail : foundEmployee.email}`,
    //     dateofbirth: dateofbirth,
    //     degree: `${newDegreeUpdated ? newDegree : foundEmployee.degree}`,
    //   }),
    // };

    // try {
