    //   );
    //   if (!fetchPromiseResponse.ok) {
    //     console.log(
    //       `Something went wrong with fetch from server ${fetchPromiseResponse.status}`
    //     );
    //   }
    //   const jsonPromiseResponse = fetchPromiseResponse.json();

    //   jsonPromiseResponse.then((res) => {
    //     console.log(res);
    //   });
    // } catch {
    //   (err) => {
    //     console.log(`FETCH FAILED: ${err}`);
    //   };
    // }
  }

  return (
    <>
      <Nav />
      <h1>ALL EMPLOYEES</h1>
      {employees.map((employee) => {
        const {
          address,
          dateofbirth,
          degree,
          department,
          designation,
          email,
          employeeid,
          firstname,
          gender,
          lastname,
          mobile,
        } = employee;

        return (
          <section
            className="employee-card"
            key={employeeid}
          >
            <input
              type="radio"
              name=""
              id=""
            />
            <form
              onSubmit={(e) =>
                updateEmployee(
                  e,
                  address,
                  dateofbirth,
                  degree,
                  department,
                  designation,
                  email,
                  employeeid,
                  firstname,
                  gender,
                  lastname,
                  mobile
                )
              }
              autoComplete="true"
            >
              <div style={{ display: "flex" }}>
                <p>Image</p>
                <img
                  src=""
                  alt="picturrrrrr"
                />
              </div>
              <div style={{ display: "flex" }}>
                <p>Employee ID: </p>
                <p> {employeeid} </p>
              </div>
              <div style={{ display: "flex" }}>
                <p>Gender: </p>
                {editMode ? (
                  <>
                    <input
                      type="text"
                      name=""
                      id=""
                      onChange={(e) => {
                        setNewGender(e.target.value);
                        setNewGenderUpdated(true);
                      }}
                      placeholder={
                        editMode & !newGenderUpdated
                          ? gender
                          : newGenderUpdated & editMode
                          ? newGender
                          : gender
                      }
                    />
                  </>
                ) : (
                  <p> {newGenderUpdated ? newGender : gender} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Name: </p>
                {editMode ? (
                  <input
                    type="text"
                    name=""
                    onChange={(e) => {
                      setNewFirstname(e.target.value);
                      setNewFirstnameUpdated(true);
                    }}
                    placeholder={
                      editMode & !newFirstnameUpdated
                        ? firstname
                        : newFirstnameUpdated & editMode
                        ? newFirstname
                        : firstname
                    }
                  />
                ) : (
                  <p> {newFirstnameUpdated ? newFirstname : firstname} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Last Name: </p>
                {editMode ? (
                  <input
                    type="text"
                    name=""
                    onChange={(e) => {
                      setNewLastname(e.target.value);
                      setNewLastnameUpdated(true);
                    }}
                    placeholder={
                      editMode & !newLastnameUpdated
                        ? lastname
                        : newLastnameUpdated & editMode
                        ? newLastname
                        : lastname
                    }
                  />
                ) : (
                  <p> {newLastnameUpdated ? newLastname : lastname} </p>
                )}
              </div>

              <div style={{ display: "flex" }}>
                <p>DOB</p>

                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewDateofbirth(e.target.value);
                      setNewDateofbirthUpdated(true);
                    }}
                    placeholder={
                      editMode & !newDateofbirthUpdated
                        ? dateofbirth
                        : newDateofbirthUpdated & editMode
                        ? newDateofbirth
                        : dateofbirth
                    }
                  />
                ) : (
                  <p> {dateofbirth} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Department </p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewDepartment(e.target.value);
                      setNewDepartmentUpdated(true);
                    }}
                    placeholder={
                      editMode & !newDateofbirthUpdated
                        ? department
                        : newDateofbirthUpdated & editMode
                        ? newDepartment
                        : department
                    }
                  />
                ) : (
                  <p> {newDateofbirthUpdated ? newDepartment : department} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Role</p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewDesignation(e.target.value);
                      setNewDesignationUpdated(true);
                    }}
                    placeholder={
                      editMode & !newDesignationUpdated
                        ? designation
                        : newDesignationUpdated & editMode
                        ? newDesignation
                        : designation
                    }
                  />
                ) : (
                  <p>
                    {" "}
                    {newDesignationUpdated ? newDesignation : designation}{" "}
                  </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Degree</p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewDegree(e.target.value);
                      setNewDegreeUpdated(true);
                    }}
                    placeholder={
                      editMode & !newDegreeUpdated
                        ? degree
                        : newDegreeUpdated & editMode
                        ? newDegree
                        : degree
                    }
                  />
                ) : (
                  <p> {newDegreeUpdated ? newDegree : degree} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Mobile</p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewMobile(e.target.value);
                      setNewMobileUpdated(true);
                    }}
                    placeholder={
                      editMode & !newMobileUpdated
                        ? degree
                        : newMobileUpdated & editMode
                        ? newDegree
                        : degree
                    }
                  />
                ) : (
                  <p> {newMobileUpdated ? newMobile : mobile} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>email</p>
                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewEmail(e.target.value);
                      setNewEmailUpdated(true);
                    }}
                    placeholder={
                      editMode & !newEmailUpdated
                        ? email
                        : newEmailUpdated & editMode
                        ? newEmail
                        : email
                    }
                  />
                ) : (
                  <p> {newEmailUpdated ? newEmail : email} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>Address</p>

                {editMode ? (
                  <input
                    type="text"
                    onChange={(e) => {
                      setNewAddress(e.target.value);
                      setNewAddressUpdated(true);
                    }}
                    placeholder={
                      editMode & !newAddressUpdated
                        ? address
                        : newAddressUpdated & editMode
                        ? newAddress
                        : address
                    }
                  />
                ) : (
                  <p> {newAddressUpdated ? newAddress : address} </p>
                )}
              </div>
              <div style={{ display: "flex" }}>
                <p>join date</p>
                <p> {date} </p>
              </div>
              {editMode ? (
                <button type="submit">UPDATE</button>
              ) : (
                <>
                  <button
                    onClick={editEmployee}
                    type="button"
                  >
                    Edit
                  </button>

                  <button type="button">delete</button>
                </>
              )}
            </form>
          </section>
        );
      })}
    </>
  );
};

export default AllEmployees;
