import { useEffect, useState } from "react";
const URL = "http://localhost:8080";

function getEmployees() {
  // get employees
  useEffect(() => {
    fetch(`${URL}/employees`)
      .then((res) => res.json())
      .then((json) => setEmployees(json));
  }, []);
  const [employees, setEmployees] = useState([]);

  return employees;
}
