import React, { useEffect, useState } from "react";
import Nav from "../Global/Nav";

const AllEmployees = () => {
  let dateObj = new Date();
  let year = dateObj.getFullYear();
  let month = dateObj.getMonth();
  let day = dateObj.getDate();

  const finalMonth = () => {
    if ((month <= 10) & (month === 0)) {
      return `0${month + 1}`;
    } else {
      return month;
    }
  };
  const finalDay = () => {
    if (day <= 10) {
      return `0${day}`;
    } else {
      return day;
    }
  };
  let date = `${year}-${finalMonth()}-${finalDay()}`;

  // get employees
  useEffect(() => {
    fetch(`${URL}/employees`)
      .then((res) => res.json())
      .then((json) => setEmployees(json));
  }, []);

  const URL = "http://localhost:8080";
  const [employees, setEmployees] = useState([]);

  // EDIT MODE
  const [editMode, setEditMode] = useState(false);
  // SET FORM
  const [address, setNewAddress] = useState("");
  const [dateofbirth, setNewDateofbirth] = useState("");
  const [degree, setNewDegree] = useState("");
  const [department, setNewDepartment] = useState("");
  const [designation, setNewDesignation] = useState("");
  const [email, setNewEmail] = useState("");
  const [firstname, setNewFirstname] = useState("");
  const [gender, setNewGender] = useState("");
  const [lastname, setNewLastname] = useState("");
  const [mobile, setNewMobile] = useState("");

  function editEmployee() {
    setEditMode(!editMode);
  }
  console.log( `EDIT MODE: ${editMode}` );
  cons

  return (
    <>
      <Nav />
      <h1>ALL EMPLOYEES</h1>
      {employees.map((employee) => {
        const {
          address,
          dateofbirth,
          degree,
          department,
          designation,
          email,
          employeeid,
          firstname,
          gender,
          lastname,
          mobile,
        } = employee;

        return (
          <section
            className="employee-card"
            key={employeeid}
          >
            <input
              type="radio"
              name=""
              id=""
            />
            <div style={{ display: "flex" }}>
              <p>Image</p>
              <img
                src=""
                alt="picturrrrrr"
              />
            </div>
            <div style={{ display: "flex" }}>
              <p>Employee ID: </p>
              <p> {employeeid} </p>
            </div>
            <div style={{ display: "flex" }}>
              <p>Gender: </p>
              {editMode ? (
                <>
                  <input
                    type="text"
                    name=""
                    id=""
                    onChange={(e) => {
                      setNewGender(e.target.value);
                    }}
                  />
                </>
              ) : (
                <p> {gender} </p>
              )}
            </div>
            <div style={{ display: "flex" }}>
              <p>Name: </p>
              {editMode ? (
                <input
                  type="text"
                  name=""
                  onChange={(e) => setNewFirstname(e.target.value)}
                />
              ) : (
                <p> {firstname}</p>
              )}
            </div>
            <div style={{ display: "flex" }}>
              <p>Name: </p>
              {editMode ? (
                <input
                  type="text"
                  name=""
                  onChange={(e) => setNewLastname(e.target.value)}
                />
              ) : (
                <p> {lastname}</p>
              )}
            </div>

            <div style={{ display: "flex" }}>
              <p>DOB</p>
              {editMode ? (
                <input
                  type="text"
                  onChange={(e) => setNewDateofbirth(e.target.value)}
                />
              ) : (
                <p> {dateofbirth} </p>
              )}
            </div>
            <div style={{ display: "flex" }}>
              <p>Department </p>
              {editMode ? (
                <input
                  type="text"
                  onChange={(e) => setNewDepartment(e.target.value)}
                />
              ) : (
                <p> {department ? department : "none"} </p>
              )}
            </div>
            <div style={{ display: "flex" }}>
              <p>Role</p>
              {editMode ? (
                <input
                  type="text"
                  onChange={(e) => setNewDesignation(e.target.value)}
                />
              ) : (
                <p> {designation} </p>
              )}
            </div>
            <div style={{ display: "flex" }}>
              <p>Degree</p>
              {editMode ? (
                <input
                  type="text"
                  onChange={(e) => setNewDegree(e.target.value)}
                />
              ) : (
                <p> {degree} </p>
              )}
            </div>
            <div style={{ display: "flex" }}>
              <p>Mobile</p>
              {editMode ? (
                <input
                  type="text"
                  onChange={(e) => setNewMobile(e.target.value)}
                />
              ) : (
                <p> {mobile} </p>
              )}
            </div>
            <div style={{ display: "flex" }}>
              <p>email</p>
              {editMode ? (
                <input
                  type="text"
                  onChange={(e) => setNewEmail(e.target.value)}
                />
              ) : (
                <p>${email}</p>
              )}
            </div>
            <div style={{ display: "flex" }}>
              <p>email</p>

              {editMode ? (
                <input
                  type="text"
                  onChange={(e) => setNewAddress(e.target.value)}
                />
              ) : (
                <p>${address} </p>
              )}
            </div>
            <div style={{ display: "flex" }}>
              <p>join date</p>
              <p> {date} </p>
            </div>
            {editMode ? (
              <button
                type="button"
                onClick={() => {
                  setEditMode(!editMode);
                }}
              >
                {" "}
                UPDATE{" "}
              </button>
            ) : (
              <>
                <button
                  onClick={editEmployee}
                  type="button"
                >
                  Edit
                </button>

                <button type="button">delete</button>
              </>
            )}
          </section>
        );
      })}
    </>
  );
};

export default AllEmployees;
