import { useEffect, useState } from "react";
const URL = "http://localhost:8080";
// get employees
useEffect(() => {
  fetch(`${URL}/employees`)
    .then((res) => res.json())
    .then((json) => setEmployees(json));
}, []);
export const [employees, setEmployees] = useState([]);
