import React, { useState, useEffect } from "react";
import Nav from "../Global/Nav";
import AllEmployees from "../Employees/AllEmployees";
import { Button } from "react-bootstrap";

const AddEmployee = () => {
  const [fname, setFname] = useState("");
  const [lname, setLname] = useState("");
  const [gender, setGender] = useState("");
  const [mobile, setMobile] = useState(0);
  const [password, setPassword] = useState("");
  const [designation, setDesignation] = useState("");
  const [department, setDepartment] = useState("");
  const [address, setAddress] = useState("");
  const [email, setEmail] = useState("");
  const [dob, setDOB] = useState("");
  const [degree, setDegree] = useState("");
  const URL = "http://localhost:8080";

  // ADD EMPLOYEE TO DB
  async function addEmployeeNow(e) {
    e.preventDefault();

    // Post options
    const options = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        fname: fname,
        lname: lname,
        gender: gender,
        mobile: mobile,
        password: password,
        designation: designation,
        department: department,
        address: address,
        email: email,
        dateofbirth: dob,
        degree: degree,
      }),
    };

    try {
      const fetchPromiseResponse = await fetch(`${URL}/add-employee`, options);
      if (!fetchPromiseResponse.ok) {
        console.log(
          `Something went wrong with fetch from server ${fetchPromiseResponse.status}`
        );
      }
      const jsonPromiseResponse = fetchPromiseResponse.json();

      jsonPromiseResponse.then((res) => {
        console.log(res);
      });
    } catch {
      (err) => {
        console.log(`FETCH FAILED: ${err}`);
      };
    }
  }

  return (
    <>
      <Nav />
    <Container></Container>
      <h1 className="test">ADD EMPLOYEE</h1>
      <form
        onSubmit={addEmployeeNow}
        autoComplete="true"
      >
        <label htmlFor="">Add Employee</label>
        <input
          type="text"
          placeholder="first name *"
          onChange={(e) => {
            setFname(e.target.value);
          }}
          required={true}
        />
        <input
          type="text"
          placeholder="last name"
          onChange={(e) => {
            setLname(e.target.value);
          }}
        />

        <input
          type="text"
          placeholder="gender"
          onChange={(e) => {
            setGender(e.target.value);
          }}
        />

        <input
          type="tel"
          placeholder="mobile*"
          onChange={(e) => {
            setMobile(e.target.value);
          }}
          required={true}
        />
        <input
          type="text"
          placeholder="password*"
          required={true}
        />
        <input
          type="text"
          placeholder="re-enter password*"
          onChange={(e) => {
            setPassword(e.target.value);
          }}
          required={true}
        />
        <input
          type="text"
          placeholder="designmation"
          onChange={(e) => {
            setDesignation(e.target.value);
          }}
        />
        <br />
        <br />

        <label htmlFor="select department">Select Department</label>

        <select
          name="select department"
          id="select department"
          placeholder="Select Department"
          onChange={(e) => {
            setDepartment(e.target.value);
          }}
          required={true}
          defaultValue="designing"
        >
          <option value="development">Development</option>
          <option value="designing">designing</option>
          <option value="testing">testing</option>
          <option value="hr">HR</option>
        </select>

        <br />
        <br />
        <input
          type="text"
          placeholder="address"
          onChange={(e) => {
            setAddress(e.target.value);
          }}
        />

        <input
          type="text"
          placeholder="email"
          onChange={(e) => {
            setEmail(e.target.value);
          }}
        />

        <input
          type="date"
          placeholder="date of birth"
          onChange={(e) => {
            setDOB(e.target.value);
          }}
        />

        <input
          type="text"
          placeholder="Degree"
          onChange={(e) => {
            setDegree(e.target.value);
          }}
        />

        <p>upload image</p>
        <input type="file" />
        <Button type="submit">Submit</Button>
        <Button type="button">Cancel</Button>
      </form>
    </>
  );
};

export default AddEmployee;
