import React, { useEffect, useState } from "react";
import Nav from "../Global/Nav";
import { Container, Form } from "react-bootstrap";
import EmployeeCard from "./EmployeeCard";
import { date } from "../../Helpers/date";

const AllEmployees = () => {
  const URL = "http://localhost:8080";

  // get employees
  const getEmployees = useEffect(() => {
    async function employeeReturn() {
      const fetchPromiseResponse = await fetch(`${URL}/employees`);

      if (!fetchPromiseResponse.ok) {
        console.log(
          `Something went wrong with fetch from server ${fetchPromiseResponse.status}`
        );
      }

      const jsonPromiseResponse = fetchPromiseResponse.json();

      const employees = jsonPromiseResponse.then((json) => setEmployees(json));

      return employees;
    }
  }, []);

  
  let filteredEmloyees = [];
  const [employees, setEmployees] = useState([]);
  console.log(employees);
  const [employeeNewArray, setemployeeNewArray] = useState([]);

  return (
    <>
      <Nav />

      <Container>
        <section className="employees">
          <div className="employees-header">
            <div>
              <h2>Employees</h2>
              <div className="header-search form-control-container">
                <span className="search-icon form-control-container-icon">
                  <i className="fa-solid fa-magnifying-glass"></i>
                </span>
                <Form.Control
                  className="header-search-input"
                  type="text"
                  placeholder="Search"
                  onChange={(e) => {
                    filteredEmloyees = employees.filter((employee) => {
                      const {
                        firstname,
                        lastname,
                        department,
                        employeeid,
                        designation,
                        email,
                        date,
                        mobile,
                      } = employee;
                      return (
                        firstname == e.target.value ||
                        lastname == e.target.value ||
                        department == e.target.value ||
                        employeeid == e.target.value ||
                        designation == e.target.value ||
                        department == e.target.value ||
                        email == e.target.value ||
                        date == e.target.value ||
                        mobile == e.target.value
                      );
                    });
                    setemployeeNewArray(filteredEmloyees);
                  }}
                />
              </div>
            </div>

            <div className="employees-header-controls">
              <span className="icon-container">
                <i
                  type="button"
                  onClick={() => {
                    handleShow;
                  }}
                  className="fa-solid fa-plus icon-container-icon"
                ></i>
              </span>
              <span className="icon-container">
                <i className="fa-solid fa-arrows-rotate icon-container-icon"></i>
              </span>
            </div>
          </div>
          {employeeNewArray.length > 0
            ? employeeNewArray.map((employee) => {
                const {
                  department,
                  designation,
                  email,
                  employeeid,
                  firstname,
                  lastname,
                  mobile,
                } = employee;

                return (
                  <EmployeeCard
                    URL={URL}
                    employees={employees}
                    key={employeeid}
                    employeeid={employeeid}
                    firstname={firstname}
                    lastname={lastname}
                    department={department}
                    designation={designation}
                    mobile={mobile}
                    email={email}
                    date={date}
                  />
                );
              })
            : employees.map((employee) => {
                const {
                  department,
                  designation,
                  email,
                  employeeid,
                  firstname,
                  lastname,
                  mobile,
                } = employee;

                return (
                  <EmployeeCard
                    URL={URL}
                    employees={employees}
                    key={employeeid}
                    employeeid={employeeid}
                    firstname={firstname}
                    lastname={lastname}
                    department={department}
                    designation={designation}
                    mobile={mobile}
                    email={email}
                    date={date}
                  />
                );
              })}
        </section>
      </Container>
    </>
  );
};

export default AllEmployees;
