import React, { useState } from "react";
import Nav from "../Global/Nav";

const AddProject = () => {
  const [title, setTitle] = useState("");
  const [department, setDepartment] = useState("");
  const [priority, setPriority] = useState("");
  const [client, setClient] = useState(0);
  const [price, setPrice] = useState(0);
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [team, setTeam] = useState("");
  const [status, setStatus] = useState("");
  const [selectedRadio, setSelectedRadio] = useState("");
  const [description, setDescription] = useState(selectedRadio);
  const URL = "http://localhost:3001";

  console.log(selectedRadio);
  // fetch for data
  async function AddProjectNow(e) {
    e.preventDefault();
    // Post options
    const options = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        title: title,
        department: department,
        priority: priority,
        client: client,
        price: price,
        startDate: startDate,
        endDate: endDate,
        team: team,
        status: status,
        description: description,
      }),
    };

    try {
      const fetchPromiseResponse = await fetch(`${URL}/add-project`, options);
      if (!fetchPromiseResponse.ok) {
        console.log(
          `Something went wrong with fetch from server ${fetchPromiseResponse.status}`
        );
      }
      const jsonPromiseResponse = fetchPromiseResponse.json();

      jsonPromiseResponse.then((res) => {
        console.log(res);
      });
    } catch {
      (err) => {
        console.log(`FETCH FAILED: ${err}`);
      };
    }
  }
  return (
    <>
      <Nav />
      <form
        action=""
        onSubmit={AddProjectNow}
      >
        <label htmlFor="">Add Project</label>
        <input
          type="text"
          placeholder="Project Id *"
          required={true}
          onChange={(e) => {
            setTitle(e.target.value);
          }}
        />
        <input
          onChange={(e) => {
            setTitle(e.target.value);
          }}
          type="text"
          placeholder="Project title"
        />
        <input
          onChange={(e) => {
            setDepartment(e.target.value);
          }}
          type="text"
          placeholder="dept"
        />
        <input
          onChange={(e) => {
            setPriority(e.target.value);
          }}
          type="tel"
          placeholder="priority*"
          required={true}
        />
        <input
          onChange={(e) => {
            setClient(e.target.value);
          }}
          type="text"
          placeholder="client*"
          required={true}
        />
        <input
          onChange={(e) => {
            setPrice(e.target.value);
          }}
          type="number"
          placeholder="price*"
          required={true}
        />
        <input
          onChange={(e) => {
            setStartDate(e.target.value);
          }}
          type="date"
          placeholder="start date"
        />
        <input
          onChange={(e) => {
            setEndDate(e.target.value);
          }}
          type="date"
          placeholder="end date"
        />
        <label htmlFor="Team">Team</label>
        <select
          onChange={(e) => {
            setTeam(e.target.value);
          }}
          name="Team"
          id="Team"
          placeholder="Team"
          required={true}
        >
          <option value="development">Development</option>
          <option value="designing">designing</option>
          <option value="testing">testing</option>
          <option value="hr">HR</option>
        </select>
        <br />
        <br />
        {/* *********WORK STATUS RADIOS */}
        <fieldset>
          <legend>Work Status</legend>

          <label htmlFor="Active">Active </label>
          <input
            type="radio"
            name="status"
            value="active"
            onClick={(e) => {
              setSelectedRadio(e.target.value);
            }}
          />
          <label htmlFor="completed">completed</label>
          <input
            type="radio"
            name="status"
            onClick={(e) => {
              setSelectedRadio(e.target.value);
            }}
            value="completed"
          />
          <label htmlFor="">running</label>
          <input
            type="radio"
            name="status"
            onClick={(e) => {
              setSelectedRadio(e.target.value);
            }}
            value="running"
          />
          <label htmlFor="">pending</label>
          <input
            type="radio"
            name="status"
            onClick={(e) => {
              setSelectedRadio(e.target.value);
            }}
            value="pending"
          />
          <label htmlFor="">not started</label>
          <input
            type="radio"
            name="status"
            onClick={(e) => {
              setSelectedRadio(e.target.value);
            }}
            value="not started"
          />
          <label htmlFor="">canceled</label>
          <input
            type="radio"
            name="status"
            onClick={(e) => {
              setSelectedRadio(e.target.value);
            }}
            value="canceled"
          />
        </fieldset>

        <label htmlFor="description">Descriptions</label>
        <input
          onClick={(e) => {
            setDescription(e.target.value);
          }}
          type="text"
          id="description"
        />
        <p>upload image</p>
        <input type="file" />
        <button type="submit">Submit</button>
        <button type="button">Cancel</button>
      </form>
    </>
  );
};

export default AddProject;
