import React, { useState } from "react";
import Nav from "../Global/Nav";
import { Container, Form } from "react-bootstrap";
import EmployeeCard from "./EmployeeCard";
import { date } from "../../Helpers/date";
import GetEmployeesNow from "../../Hooks/GetEmployeesNow";

const AllEmployees = () => {
  const employees = GetEmployeesNow();
  let filteredEmloyees = [];

  const [employeeNewArray, setemployeeNewArray] = useState([]);

  // GET FILTERED EMPLLOYEES
  const getFilteredEmployees = (e) => {
    filteredEmloyees = employees.filter((employee) => {
      const {
        firstname,
        lastname,
        department,
        employeeid,
        designation,
        email,
        date,
        mobile,
      } = employee;
      return (
        firstname.includes(e.target.value) ||
        lastname.includes(e.target.value) ||
        department == e.target.value ||
        employeeid == e.target.value ||
        designation == e.target.value ||
        department == e.target.value ||
        email == e.target.value ||
        date == e.target.value ||
        mobile == e.target.value
      );
    });
  };

  // UPDATE EMPLOYEE
  async function updateEmployee(e, id) {
    e.preventDefault();

    // Post options
    const options = {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        employeeid: employee[0].employeeid,
        fname: `${newFirstnameUpdated ? newFirstname : employee[0].firstname}`,
        lname: `${newLastnameUpdated ? newLastname : employee[0].lastname}`,
        mobile: `${newMobileUpdated ? newMobile : employee[0].mobile}`,
        designation: `${
          newDesignationUpdated ? newDesignation : employee[0].designation
        }`,
        department: `${
          newDepartmentUpdated ? newDepartment : employee[0].department
        }`,
        email: `${newEmailUpdated ? newEmail : employee[0].email}`,
      }),
    };

    try {
      const fetchPromiseResponse = await fetch(
        `${URL}/update-employee`,
        options
      );
      if (!fetchPromiseResponse.ok) {
        console.log(
          `Something went wrong with fetch from server ${fetchPromiseResponse.status}`
        );
      }
      const jsonPromiseResponse = fetchPromiseResponse.json();

      jsonPromiseResponse.then((res) => {
        console.log(res);
      });
    } catch {
      (err) => {
        console.log(`FETCH FAILED: ${err}`);
      };
    }
  }

  return (
    <>
      <Nav />

      <Container>
        <section className="employees">
          <div className="employees-header">
            <div>
              <h2>Employees</h2>
              <div className="header-search form-control-container">
                <span className="search-icon form-control-container-icon">
                  <i className="fa-solid fa-magnifying-glass"></i>
                </span>
                <Form.Control
                  className="header-search-input"
                  type="text"
                  placeholder="Search"
                  onChange={(e) => {
                    getFilteredEmployees(e);
                    setemployeeNewArray(filteredEmloyees);
                  }}
                />
              </div>
            </div>

            <div className="employees-header-controls">
              <span className="icon-container">
                <i
                  type="button"
                  onClick={() => {
                    handleShow;
                  }}
                  className="fa-solid fa-plus icon-container-icon"
                ></i>
              </span>
              <span className="icon-container">
                <i className="fa-solid fa-arrows-rotate icon-container-icon"></i>
              </span>
            </div>
          </div>
          {employeeNewArray.length > 0
            ? employeeNewArray.map((employee) => {
                const {
                  department,
                  designation,
                  email,
                  employeeid,
                  firstname,
                  lastname,
                  mobile,
                } = employee;

                return (
                  <EmployeeCard
                    URL={URL}
                    employees={employees}
                    key={employeeid}
                    employeeid={employeeid}
                    firstname={firstname}
                    lastname={lastname}
                    department={department}
                    designation={designation}
                    mobile={mobile}
                    email={email}
                    date={date}
                  />
                );
              })
            : employees.map((employee) => {
                const {
                  department,
                  designation,
                  email,
                  employeeid,
                  firstname,
                  lastname,
                  mobile,
                } = employee;

                return (
                  <EmployeeCard
                    URL={URL}
                    employees={employees}
                    key={employeeid}
                    employeeid={employeeid}
                    firstname={firstname}
                    lastname={lastname}
                    department={department}
                    designation={designation}
                    mobile={mobile}
                    email={email}
                    date={date}
                  />
                );
              })}
        </section>
      </Container>
    </>
  );
};

export default AllEmployees;
