import React, { useEffect, useState } from "react";
import Nav from "../Global/Nav";

const AllEmployees = () => {
  let dateObj = new Date();
  let year = dateObj.getFullYear();
  let month = dateObj.getMonth();
  let day = dateObj.getDate();

  const finalMonth = () => {
    if ((month <= 10) & (month === 0)) {
      return `0${month + 1}`;
    } else {
      return month;
    }
  };
  const finalDay = () => {
    if (day <= 10) {
      return `0${day}`;
    } else {
      return day;
    }
  };
  let date = `${year}-${finalMonth()}-${finalDay()}`;

  // get employees
  useEffect(() => {
    fetch(`${URL}/employees`)
      .then((res) => res.json())
      .then((json) => setEmployees(json));
  }, []);

  const URL = "http://localhost:8080";
  const [employees, setEmployees] = useState([]);

  // EDIT MODE
  const [editMode, setEditMode] = useState(false);
  const [inputEditMode, setInputEditMode] = useState(true);
  const [gender, setGender] = useState("");

  function editEmployee() {
    setEditMode(!editMode);
  }
  console.log(`EDIT MODE: ${editMode}`);
  console.log(`EDIT MODE: ${editMode}`);
  console.log(gender);

  return (
    <>
      <Nav />
      <h1>ALL EMPLOYEES</h1>
      {employees.map((employee) => {
        const {
          address,
          dateofbirth,
          degree,
          department,
          designation,
          email,
          employeeid,
          firstname,
          gender,
          lastname,
          mobile,
        } = employee;

        return (
          <section
            className="employee-card"
            key={employeeid}
          >
            <input
              type="radio"
              name=""
              id=""
            />
            <div style={{ display: "flex" }}>
              <p>Image</p>
              <img
                src=""
                alt="picturrrrrr"
              />
            </div>
            <div style={{ display: "flex" }}>
              <p>Gender: </p>
              {editMode ? (
                <>
                  <input
                    type="text"
                    name=""
                    id=""
                    onChange={(e) => {
                      setGender(e.target.value);
                    }}
                  />
                  <button
                    onClick={() => setInputEditMode(!inputEditMode)}
                    type="button"
                  >
                    Done
                  </button>
                </>
              ) : (
                <p> {gender} </p>
              )}
            </div>
            <div style={{ display: "flex" }}>
              <p>Name: </p>
              <p>
                {" "}
                {firstname} {lastname}{" "}
              </p>
            </div>

            <div style={{ display: "flex" }}>
              <p>DOB</p>
              <p> {date} </p>
            </div>
            <div style={{ display: "flex" }}>
              <p>Department </p>
              <p> {department ? department : "none"} </p>
            </div>
            <div style={{ display: "flex" }}>
              <p>Role</p>
              <p> {designation} </p>
            </div>
            <div style={{ display: "flex" }}>
              <p>Degree</p>
              <p> {degree} </p>
            </div>
            <div style={{ display: "flex" }}>
              <p>Mobile</p>
              <p> {mobile} </p>
            </div>
            <div style={{ display: "flex" }}>
              <p>email</p>
              <p>${email}</p>
            </div>
            <div style={{ display: "flex" }}>
              <p>email</p>
              <p>${address}</p>
            </div>
            <div style={{ display: "flex" }}>
              <p>join date</p>
              <p> {date} </p>
            </div>
            <button
              onClick={editEmployee}
              type="button"
            >
              Edit
            </button>
            <button type="button">delete</button>
          </section>
        );
      })}
    </>
  );
};

export default AllEmployees;
