import { Container, Button, Row, Form, Col } from "react-bootstrap";
import React, { useEffect, useState } from "react";
import UpdateModal from "./UpdateModal";

const EmployeeCard = ({
  URL,
  employees,
  employeeid,
  firstname,
  lastname,
  department,
  designation,
  mobile,
  email,

  date,
}) => {
  // EDIT MODE
  const [editMode, setEditMode] = useState(false);
  const [btnValue, setBtnValue] = useState(0);
  const [employee, setEmployee] = useState("");

  // SET UPDATED INPUTS
  const [newDepartmentUpdated, setNewDepartmentUpdated] = useState(false);
  const [newDesignationUpdated, setNewDesignationUpdated] = useState(false);
  const [newEmailUpdated, setNewEmailUpdated] = useState(false);
  const [newFirstnameUpdated, setNewFirstnameUpdated] = useState(false);
  const [newLastnameUpdated, setNewLastnameUpdated] = useState(false);
  const [newMobileUpdated, setNewMobileUpdated] = useState(false);

  const [combinedName, setCombinedName] = useState("");
  let splitName = combinedName.split(" ");
  // SET NEW FORM
  const [newDepartment, setNewDepartment] = useState("");
  const [newDesignation, setNewDesignation] = useState("");
  const [newEmail, setNewEmail] = useState("");
  const [newFirstname, setNewFirstname] = useState("");
  const [newLastname, setNewLastname] = useState("");
  const [newMobile, setNewMobile] = useState("");

  // SEARCH EMPLOYEE
  const findEmployeeForUpdate = (id) => {
    let foundEmployee = employees.filter((employee) => {
      const { employeeid } = employee;
      if (employeeid === id) {
        return employee;
      }
    });
    setBtnValue(id);
    setEditMode(!editMode);
    setEmployee(foundEmployee);
    return foundEmployee;
  };

  // UPDATE EMPLOYEE
  async function updateEmployee(e, id) {
    e.preventDefault();

    // Post options
    const options = {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        employeeid: employee[0].employeeid,
        fname: `${newFirstnameUpdated ? newFirstname : employee[0].firstname}`,
        lname: `${newLastnameUpdated ? newLastname : employee[0].lastname}`,
        mobile: `${newMobileUpdated ? newMobile : employee[0].mobile}`,
        designation: `${
          newDesignationUpdated ? newDesignation : employee[0].designation
        }`,
        department: `${
          newDepartmentUpdated ? newDepartment : employee[0].department
        }`,
        email: `${newEmailUpdated ? newEmail : employee[0].email}`,
      }),
    };

    try {
      const fetchPromiseResponse = await fetch(
        `${URL}/update-employee`,
        options
      );
      if (!fetchPromiseResponse.ok) {
        console.log(
          `Something went wrong with fetch from server ${fetchPromiseResponse.status}`
        );
      }
      const jsonPromiseResponse = fetchPromiseResponse.json();

      jsonPromiseResponse.then((res) => {
        console.log(res);
      });
    } catch {
      (err) => {
        console.log(`FETCH FAILED: ${err}`);
      };
    }
  }
  // DELETE EMPLOYEE
  async function deleteEmployee(id) {
    // Post options
    const options = {
      method: "DELETE",
    };

    try {
      const fetchPromiseResponse = await fetch(
        `${URL}/delete-employee/${id}`,
        options
      );
      if (!fetchPromiseResponse.ok) {
        console.log(
          `Something went wrong with fetch from server ${fetchPromiseResponse.status}`
        );
      }
      const jsonPromiseResponse = fetchPromiseResponse.json();

      jsonPromiseResponse.then((res) => {
        console.log(res);
      });
    } catch {
      (err) => {
        console.log(`FETCH FAILED: ${err}`);
      };
    }
  }

  function showUpdateModal() {
    return <UpdateModal />;
  }

  return (
    <section>
      <Form
        autoComplete="true"
        className="employees_form"
      >
        <Row className="justify-content-between">
          <Col
            lg="1"
            style={{ width: "3%" }}
          >
            <Row style={{ justifyContent: "center" }}>
              <Col
                lg="12"
                className="col-6"
              >
                <input
                  style={{ margin: "0" }}
                  className="radio"
                  type="radio"
                  name=""
                  id=""
                />
              </Col>
            </Row>
          </Col>
          <hr className="d-block d-lg-none" />
          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>Image</p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                <img
                  src=""
                  alt="picturrrrrr"
                />
              </Col>
            </Row>
          </Col>
          <hr className="d-block d-lg-none" />
          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>Employee ID: </p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                <p> {employeeid} </p>
              </Col>
            </Row>
          </Col>
          <hr className="d-block d-lg-none" />
          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>Name: </p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                <p>
                  {" "}
                  {newFirstnameUpdated
                    ? newFirstname + " " + newLastname
                    : firstname + " " + lastname}{" "}
                </p>
              </Col>
            </Row>
          </Col>
          <hr className="d-block d-lg-none" />
          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>Department </p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                <p>{newDepartmentUpdated ? newDepartment : department}</p>
              </Col>
            </Row>
          </Col>
          <hr className="d-block d-lg-none" />
          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>Role</p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                <p>{newDesignationUpdated ? newDesignation : designation}</p>
              </Col>
            </Row>
          </Col>
          <hr className="d-block d-lg-none" />
          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>Mobile</p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                <p> {newMobileUpdated ? newMobile : mobile} </p>
              </Col>
            </Row>
          </Col>
          <hr className="d-block d-lg-none" />
          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>email</p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                <p> {newEmailUpdated ? newEmail : email} </p>
              </Col>
            </Row>
          </Col>
          <hr className="d-block d-lg-none" />
          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6"
              >
                <p>join date</p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                <p> {date} </p>
              </Col>
            </Row>
          </Col>
          <hr className="d-block d-lg-none" />
          <Col lg="1">
            <Row>
              <Col
                lg="12"
                className="col-6 d-none d-lg-block"
              >
                <p>Actions</p>
              </Col>
              <Col
                lg="12"
                className="col-6"
              >
                {(btnValue === employeeid) & (editMode == true) ? (
                  <Button
                    className="update-btn"
                    onClick={(e) => {
                      setEditMode(!editMode);
                      updateEmployee(e, employeeid);
                      updateEmployee(e, employeeid);
                    }}
                    type="button"
                  >
                    UPDATE
                  </Button>
                ) : (
                  <div className="form-btns">
                    <i
                      onClick={(e) => {
                        findEmployeeForUpdate(employeeid);
                        showUpdateModal;
                      }}
                      type="button"
                      className="fa-regular fa-pen-to-square fs-5 edit-btn"
                    ></i>
                    <i
                      onClick={() => {
                        deleteEmployee(employeeid);
                      }}
                      type="submit"
                      className="fa-solid fa-trash delete-btn"
                    ></i>
                  </div>
                )}
              </Col>
            </Row>
          </Col>

          {/* END CONTAINER ROW */}
        </Row>
      </Form>
    </section>
  );
};

export default EmployeeCard;
